package flowController;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 0;
		
		do {
			System.out.println(i);
		}
		while(i++ < 5);
		
		
		int c = 0;
		do {
			
			System.out.println(c);
			if(c++ == 5) break;
		}
		while(true);
		
		
		

	}

}
