package flowController;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 5;
		
		while(n > 0)
		{
			System.out.println(n);
			n--;
		}
		
		int c = 0;
		while(true)
		{
			System.out.println(c);
			if(c == 5) break;
			c++;
			
		}
		
		

	}

}
